import React, { useState } from "react";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import FirstStep from "./FirstStep";
import SecondStep from "./SecondStep";
import ThirdStep from "./ThirdStep";
import FounderConfirm from "./FounderConfirm";
import Success from "./Success";
import formValidation from "./Helper/formValidation";
import FounderCardCarousel from "../FounderCarouselComponents/FouderCardCarousel"

// Step titles
const labels = ["First Step", "Second Step", "Third Step" ,"Confirmation"];

const initialValues = {
    firstName: "",
    lastName: "",
    email: "",
    gender: "",
    date: "",
    city: "",
    phone: "",
    companyName: "",
    foundedOn: "",
    shortDescription: "",
    industry: ""
};

const fieldsValidation = {
    firstName: {
        error: "",
        validate: "text",
        minLength: 2,
        maxLength: 20
    },
    lastName: {
        error: "",
        validate: "text",
        minLength: 2,
        maxLength: 20
    },
    email: {
        error: "",
        validate: "email"
    },
    gender: {},
    date: {},
    city: {
        error: "",
        validate: "text",
        minLength: 3,
        maxLength: 20
    },
    phone: {
        error: "",
        validate: "phone",
        maxLength: 15
    },
    companyName: {
        error: "",
        validate: "text",
        minLength: 2,
        maxLength: 20
    },
    foundedOn: {},
    industry: {
        error: "",
        validate: "text",
        minLength: 2,
        maxLength: 20
    },

};

const StepForm = () => {
    const [activeStep, setActiveStep] = useState(0);
    const [formValues, setFormValues] = useState(initialValues);
    const [formErrors, setFormErrors] = useState({});

    // Proceed to next step
    const handleNext = () => setActiveStep((prev) => prev + 1);
    // Go back to prev step
    const handleBack = () => setActiveStep((prev) => prev - 1);

    // Handle form change
    const handleChange = (e) => {
        const { name, value } = e.target;

        // Set values
        setFormValues((prev) => ({
            ...prev,
            [name]: value
        }));

        // set errors
        const error = formValidation(name, value, fieldsValidation) || "";

        setFormErrors({
            [name]: error
        });
    };

    const handleSteps = (step) => {
        switch (step) {
            case 0:
                return (
                    <FirstStep
                        handleNext={handleNext}
                        handleChange={handleChange}
                        values={formValues}
                        formErrors={formErrors}
                    />
                );
            case 1:
                return (
                    <SecondStep
                        handleNext={handleNext}
                        handleBack={handleBack}
                        handleChange={handleChange}
                        values={formValues}
                        formErrors={formErrors}
                    />
                );
            case 2:
                return (
                    <ThirdStep
                        handleNext={handleNext}
                        handleBack={handleBack}
                        handleChange={handleChange}
                        values={formValues}
                        formErrors={formErrors}
                    />
                );
            case 3:
                return (
                    <FounderConfirm
                        handleNext={handleNext}
                        handleBack={handleBack}
                        values={formValues}
                    />
                );
            default:
                break;
        }
    };

    return (
        <>
            {activeStep === labels.length ? (
                // Last Component
                    <FounderCardCarousel />
               // <Success values={formValues} />
            ) : (
                <>
                    <Box style={{ margin: "30px 0 50px" }}>
                        {/*} <Typography variant="h4" align="center">
                            Multi Step Form
                        </Typography>
                        <Typography
                            variant="subtitle2"
                            align="center"
                            style={{ margin: "10px 0" }}
                        >
                            React Material UI multi step form with basic form validation
                            logic.
                        </Typography>*/}
                    </Box>
                    <Stepper
                        activeStep={activeStep}
                        style={{ margin: "30px 0 15px" }}
                        alternativeLabel
                    >
                        {labels.map((label) => (
                            <Step key={label}>
                                <StepLabel>{label}</StepLabel>
                            </Step>
                        ))}
                    </Stepper>
                    {handleSteps(activeStep)}
                </>
            )}
        </>
    );
};

export default StepForm;
